;WITH trg AS
(
	SELECT	*,
			CHECKSUM(VersionNumber,Name,ReleaseDate) [__RowCheckSum]
	FROM	dbo.App_SQLServerVersion
)
MERGE	trg
USING	(
		SELECT	c.value('VersionNumber[1]', 'varchar(25)') VersionNumber,c.value('Name[1]', 'varchar(255)') Name,c.value('ReleaseDate[1]', 'date') ReleaseDate
				,c.value('__RowCheckSum[1]', 'int') __RowCheckSum
		FROM	@TableData.nodes('/data/row') as T(C) 
		) src
ON src.VersionNumber = trg.VersionNumber
WHEN MATCHED AND trg.__RowCheckSum != src.__RowCheckSum THEN 
	UPDATE SET
			VersionNumber = src.VersionNumber,Name = src.Name,ReleaseDate = src.ReleaseDate
WHEN NOT MATCHED THEN 
	INSERT (VersionNumber,Name,ReleaseDate) VALUES (src.VersionNumber,src.Name,src.ReleaseDate)
WHEN NOT MATCHED BY SOURCE THEN
